mapper.select:
select count(*) from planning
where
  semesterid=#{semesterId}
and
  majorid=#{majorId}
and
  grade=#{grade}
and
  cno=#{cno}

delete from planning
where
  semesterid=#{semesterId}
and
  majorid=#{majorId}
and
  grade=#{grade}
and
  cno=#{cno}

select ifopen from planning
where
  semesterid=#{semesterId}
and
  majorid=#{majorId}
and
  grade=#{grade}
and
  cno=#{cno}

update planning set ifopen=#{ifOpen}
where
  semesterid=#{semesterId}
and
  majorid=#{majorId}
and
  grade=#{grade}
and
  cno=#{cno}
